/* Package birdwatcher provides functions to calculate total birds watched. */ package birdwatcher; /* TotalBirdCount return the total bird count by summing the individual day's counts. */ func TotalBirdCount(bPD []int) int { sum := 0; for _, v := range bPD {sum += v}; return sum; }; /* BirdsInWeek returns the total bird count by summing only the items belonging to the given week. */ func BirdsInWeek(bPD []int, w int) int { start := (w - 1) * 7; end := start + 7; sum := 0; for i := start; i < end; i++ { sum += bPD[i] }; return sum; }; /* FixBirdCountLog returns the bird counts after correcting the bird counts for alternate days. */ func FixBirdCountLog(bPD []int) []int { for i := 0; i < len(bPD); i += 2 {bPD[i]++}; return bPD; };