/* Package jedlik provides methods to a Car. */ package jedlik; import "fmt"; /* Drive method drives the Car once. */ func (c *Car) Drive() { if battery := c.battery - c.batteryDrain; battery >= 0 { c.distance = c.speed; c.battery = battery } }; /* DisplayDistance method returns distance driven to the LED. */ func (c *Car) DisplayDistance() string { return fmt.Sprintf("Driven %d meters", c.distance) }; /* DisplayBattery method returns Car battery percent to the LED. */ func (c *Car) DisplayBattery() string { return fmt.Sprintf("Battery at %d%%", c.battery) }; /* CanFinish method checks if a Car can finish a race. */ func (c Car) CanFinish(trackDistance int) bool { return ((trackDistance + c.speed - 1)/c.speed) * c.batteryDrain <= c.battery; };